import React from "react";
import Ente from "../img/ente2.png";

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.submitForm = this.submitForm.bind(this);
    this.state = {
      status: ""
    };
  }

  render() {
    const { status } = this.state;
    return (
      <div>
        <div style = {{textAlign: "center", paddingTop:"3%"}}>
          <h2 style = {{fontSize: "2.5em", color: "#383e56"}}>Kontaktiere mich.</h2>
          <p className="contact-h3">Falls dir etwas unklar erscheint oder du mich gerne persönlich kennenlernen willst, melde dich gerne auf den unten beschriebenen Arten bei mir.</p>
        </div>
        <div className = "row " style = {{padding: "3%", textAlign: "center"}}>
          <div className = "col-lg-5 col-sm-12 col-md-5">
            <div>
              <form
                className="form2"
                onSubmit={this.submitForm}
                action="https://formspree.io/mwkrzvgd"
                method="POST"
              >
                <div className="heading2"><strong>E-MAIL VERFASSEN</strong>
                </div>
                <div className="p2" type="Name: ">
                  <input className="input2" type="text" name="name" placeholder="Füge hier deinen Namen ein.."></input>
                </div>
                <div className="p2" type="E-Mail: ">
                  <input className="input2" type="email" name="email" placeholder="Wie kann ich dich kontaktieren.."></input>
                </div>
                <div className="p2" type="Nachricht: ">
                  <textarea className="input3" type="text" name="message" placeholder="Was möchtest du mir mitteilen.."></textarea>
                </div>
                {status === "SUCCESS" ? <p><br />Danke!</p> : <button className="submit button2">Absenden</button>}
                {status === "ERROR" && <p><br />Ohje. Ein Fehler ist aufgetreten.</p>}
                <div className="div2">
                  Ich freue mich auf deine Nachricht! <img src={Ente} className="ente-img"/>
                </div>
              </form>
            </div>
          </div>
          <div className = "col-lg-7 col-sm-12 col-md-7">
            <iframe className="google-maps" allowTransparency={true} height="400em" frameBorder={0} style = {{boxShadow: "0 2px 5px 0 rgba(0,0,0,0.16)", float:"left", background: "white"}} src="https://www.google.com/maps/embed/v1/place?key=AIzaSyAh5JW-Gubod8aAx6eFW-F3skyKoaIz6LI&q=Alfons+Petzold+Gasse,Brunn+am+Gebirge,Niederoesterreich+Oesterreich&zoom=18" allowFullScreen />
            <div className="container second-portion">
              <div className="row information-circles">
                <div className="col-xs-12 col-sm-12 col-lg-4">
                    <div className="box">
                      <div className="icon margin-to-maps">
                        <div className="image"><i className="fa fa-map-marker" aria-hidden="true" /></div>
                        <div className="info">
                          <h3 className="title">Adresse</h3>
                          <p>
                            <i className="fa fa-map-marker" aria-hidden="true" />
                            <br></br>
                            2345<br></br>
                            Brunn am Gebirge<br></br>
                            Niederösterreich
                          </p>
                        </div>
                      </div>
                      <div className="space" />
                    </div>
                  </div>
                  <div className="col-xs-12 col-sm-12 col-lg-4">
                    <div className="box">
                      <div className="icon">
                        <div className="image"><i className="fa fa-mobile" aria-hidden="true" /></div>
                        <div className="info">
                          <h3 className="title">Telefon</h3>
                          <p>
                            <i className="fa fa-mobile" aria-hidden="true" />
                            <br></br>
                            (+43) 680-1116655
                          </p>
                        </div>
                      </div>
                      <div className="space" />
                    </div>
                  </div>
                  <div className="col-xs-12 col-sm-12 col-lg-4">
                    <div className="box">
                      <div className="icon">
                        <div className="image"><i className="fa fa-envelope" aria-hidden="true" /></div>
                        <div className="info">
                          <h3 className="title">E-Mail</h3>
                          <p>
                            <i className="fa fa-envelope" aria-hidden="true" />
                            <br></br>anitalangmaier@gmail.com
                          </p>
                        </div>
                      </div>
                      <div className="space" />
                    </div>
                  </div>
                </div>
              </div>
            </div>
        </div>
      </div>
    );
  }

  submitForm(ev) {
    ev.preventDefault();
    const form = ev.target;
    const data = new FormData(form);
    const xhr = new XMLHttpRequest();
    xhr.open(form.method, form.action);
    xhr.setRequestHeader("Accept", "application/json");
    xhr.onreadystatechange = () => {
      if (xhr.readyState !== XMLHttpRequest.DONE) return;
      if (xhr.status === 200) {
        form.reset();
        this.setState({ status: "SUCCESS" });
      } else {
        this.setState({ status: "ERROR" });
      }
    };
    xhr.send(data);
  }
}
