import React from "react";

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.submitForm = this.submitForm.bind(this);
    this.state = {
      status: ""
    };
  }

  render() {
    const { status } = this.state;
    return (
      <div>
        <div className="contactBody">
          <div className="wrapper">
            <form
              className="form"
              onSubmit={this.submitForm}
              action="https://formspree.io/mwkrzvgd"
              method="POST"
            >
              <input type="text" className="name entry" name="message" placeholder="Name:"/>
              <input type="email" className="email entry" name="email" placeholder="E-Mail:"/>
              <input type="text" className="message entry" name="message" placeholder="Nachricht:"/>
              {status === "SUCCESS" ? <p>Danke!</p> : <button className="submit entry">Submit</button>}
              {status === "ERROR" && <p>Ohje. Ein Fehler ist aufgetreten.</p>}
            </form>
            <div className="shadow"></div>
          </div>
        </div>
        <div>

        </div>
      </div>
    );
  }

  submitForm(ev) {
    ev.preventDefault();
    const form = ev.target;
    const data = new FormData(form);
    const xhr = new XMLHttpRequest();
    xhr.open(form.method, form.action);
    xhr.setRequestHeader("Accept", "application/json");
    xhr.onreadystatechange = () => {
      if (xhr.readyState !== XMLHttpRequest.DONE) return;
      if (xhr.status === 200) {
        form.reset();
        this.setState({ status: "SUCCESS" });
      } else {
        this.setState({ status: "ERROR" });
      }
    };
    xhr.send(data);
  }
}
